const initialState = {
    firstName: null,
    lastName: null,
    profilePic: null,
    department: null,
    jobTitle: null

}

export default function (state = initialState, action) {
    switch(action.type) {
        case 'UPDATE_FIRSTNAME':
            return {
                ...state,
                firstName: action.value
            }

            case 'UPDATE_LASTNAME':
            return {
                ...state,
                lastName: action.value
            }

            case 'UPDATE_PROFILEPIC':
            return {
                ...state,
                profilePic: action.value
            }

            case 'UPDATE_DEPARTMENT':
                return {
                    ...state,
                    department: action.value
                }

                case 'UPDATE_JOBTITLE':
                    return {
                        ...state,
                        jobTitle: action.value
                    }


        default: 
            return state
    }
}
