import { Input, Avatar, Button } from 'react-native-elements';
import React, { useEffect, useState } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { useSelector, useDispatch} from 'react-redux'
import { updateFirstName } from '../../store/actions'
import GetLocation from 'react-native-get-location'
import * as Location from 'expo-location';





const ConfigurationFirstStep = ({ navigation }) => {
const [currentLocationTest, setLocation] = useState('')

    const dispatch = useDispatch()
    const x = useSelector((state) => {
        console.log(state)
        return state
    })

    const onFirstNameChange = (e) => {
        dispatch({ type: 'UPDATE_FIRSTNAME', value: e.target.value })
    }

const currentLocation=()=>{
    const [location, setLocation] = useState(null);
  const [errorMsg, setErrorMsg] = useState(null);

  useEffect(() => {
    (async () => {
      let { status } = await Location.requestForegroundPermissionsAsync();
      if (status !== 'granted') {
        setErrorMsg('Permission to access location was denied');
        return;
      }

      let location = await Location.getCurrentPositionAsync({});
      setLocation(location);
    })();
  }, []);

  let text = 'Waiting..';
  if (errorMsg) {
    text = errorMsg;
  } else if (location) {
    text = JSON.stringify(location);
    setLocation(text)
    console.log(currentLocationTest)
  }

  return (
    <View style={styles.container}>
      <Text style={styles.paragraph}>{text}</Text>
    </View>
  );
}

    return (
        <View>
            <View>
                <Avatar
                avatarStyle={{borderRadius: '50%'}}
                    source={{
                        uri:
                            'data:image/jpeg;base64,',
                    }}
                    size="xlarge"
                >
                    <Avatar.Accessory size={50} />
                </Avatar>
            </View>
            <View>
                <Input
                    placeholder='First Name'
                    onChange={onFirstNameChange}
                />
            </View>
            <View>
                <Input
                    placeholder='Last Names'
                />
            </View>
            <View>
                <Button
                    title="Choose Location"
                    type="outline"
                    onPress={()=> console.log(currentLocation)}
                />
            </View>
            <View>
                <Button
                    title="Next"
                    type="outline"
                    onPress={() => navigation.navigate('Step2')}
                />
            </View>
        </View>

    )
}

// const styles = StyleSheet.create({
